<?php

declare(strict_types=1);

namespace iggyvolz\obs\Requests;

/**
 * @autogenerated
 */
trait GetSceneItemTransform
{
    /**
     * @param string $sceneName Name of the scene the item is in
     * @param int $sceneItemId Numeric ID of the scene item
     * @return \Amp\Future<\iggyvolz\obs\Responses\GetSceneItemTransformResponse>
     */
    public function getSceneItemTransform(string $sceneName, int $sceneItemId): \Amp\Future
    {
        return $this->request([
            "sceneName" => $sceneName,
            "sceneItemId" => $sceneItemId,
        ], "GetSceneItemTransform", \iggyvolz\obs\Responses\GetSceneItemTransformResponse::class);
    }
}
